<script setup>

// Modal 狀態管理
const isModalOpen = ref(false)
const email = ref('')
const message = ref('')

// Modal 控制方法
const openModal = () => {
    isModalOpen.value = true
}

const closeModal = () => {
    isModalOpen.value = false
    // 清除表單資料
    email.value = ''
    message.value = ''
}

const handleSubmit = () => {
    console.log('提交資料:', {
        email: email.value,
        message: message.value
    })

    // 顯示成功訊息或處理提交邏輯
    alert('資料提交成功！')
    closeModal()
}

